import UIKit

protocol LoginProtocol: class {
    func displayAlert(strTitle : String, strMessage : String)
}

class LoginViewController: UIViewController, LoginProtocol {
    //var interactor : LoginInteractorProtocol?
    var presenter : LoginPresentationProtocol?
    
    
    @IBOutlet weak var txtEmail : UITextField?
    @IBOutlet weak var txtPassword : UITextField?
    // MARK: Object lifecycle
    
    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?) {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }
    
    required init?(coder aDecoder: NSCoder) {
        super.init(coder: aDecoder)
        setup()
    }
    
    // MARK: Setup
    
    private func setup() {
        let viewController = self
        let interactor = LoginInteractor()
        let presenter = LoginPresenter()
        
        //View Controller will communicate with only presenter
        viewController.presenter = presenter
        //viewController.interactor = interactor
        
        //Presenter will communicate with Interector and Viewcontroller
        presenter.viewController = viewController
        presenter.interactor = interactor
        
        //Interactor will communucate with only presenter.
        interactor.presenter = presenter
    }
    
    
    // MARK: View lifecycle
    
    override func viewDidLoad() {
        super.viewDidLoad()
        
        if ez.isDebug {
            txtEmail?.text = "ht11@gmail.com"
            txtPassword?.text = "12345678"
        }
    }
    
    // MARK: Do something
    
    //@IBOutlet weak var nameTextField: UITextField!
    
    @IBAction func actionLogin() {
        let params = RequestParameter.sharedInstance().loginParam(email: (txtEmail?.text)!, password: (txtPassword?.text)!)
        self.presenter?.callLoginRequest(loginParams: params)
    }
    
    @IBAction func actionSignUp() {
        self.presenter?.callSignUp()
    }
    
    @IBAction func actionForgetPassword() {
        self.presenter?.callForgetPassword()
    }
    
    func displayAlert(strTitle : String, strMessage : String) {
        //nameTextField.text = viewModel.name
        self.showAlert(title: strTitle, message: strMessage)
    }
    
    @IBAction func actionBack() {
        self.popVC()
    }
}
