import UIKit

protocol FirstCallProtocol: class {
    func getContentPageResponse(response:ContentPageResponse)
}

class FirstCallViewController: UIViewController, FirstCallProtocol {
    //var interactor : FirstCallInteractorProtocol?
    var presenter : FirstCallPresentationProtocol?
    
    @IBOutlet weak var descView: UITextView!
    var parentNavigationController : UINavigationController?
    
    // MARK: Object lifecycle
    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?) {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }
    
    required init?(coder aDecoder: NSCoder) {
        super.init(coder: aDecoder)
        setup()
    }
    
    // MARK: Setup
    private func setup() {
        let viewController = self
        let interactor = FirstCallInteractor()
        let presenter = FirstCallPresenter()
        
        //View Controller will communicate with only presenter
        viewController.presenter = presenter
        //viewController.interactor = interactor
        
        //Presenter will communicate with Interector and Viewcontroller
        presenter.viewController = viewController
        presenter.interactor = interactor
        
        //Interactor will communucate with only presenter.
        interactor.presenter = presenter
    }
    
    // MARK: View lifecycle
    override func viewDidLoad() {
        super.viewDidLoad()
        callStaticPageAPI()
    }
}

extension FirstCallViewController {
    func callStaticPageAPI() {
        self.presenter?.callStaticPageAPI()
    }
    
    func getContentPageResponse(response:ContentPageResponse) {
        self.descView.text = response.responseData?.txContent?.html2AttributedString
    }
}
