import UIKit

protocol FirstDatePresentationProtocol {
    func callStaticPageAPI()
    func getContentPageResponse(response:ContentPageResponse)
}

class FirstDatePresenter: FirstDatePresentationProtocol {
    weak var viewController: FirstDateProtocol?
    var interactor: FirstDateInteractorProtocol?
    
    // MARK: Present something
    func callStaticPageAPI() {
        self.interactor?.callStaticPageAPI()
    }
    
    func getContentPageResponse(response:ContentPageResponse){
        self.viewController?.getContentPageResponse(response: response)
    }
}
