import UIKit

protocol SelectGenderPresentationProtocol {
    func presentSomething()
    func callQuestionnaireRequest()
    func getQuestionnaireResponse(userData : [QuestionnaireModel]?)
    func actionContinue()
    func actionSkip()
}

class SelectGenderPresenter: SelectGenderPresentationProtocol {
    weak var viewController: SelectGenderProtocol?
    var interactor: SelectGenderInteractorProtocol?
    
    // MARK: Present something
    func presentSomething() {
        
    }
  
  
    func callQuestionnaireRequest() {
          self.interactor?.callQuestionnaireApi()
       }
       
       func getQuestionnaireResponse(userData : [QuestionnaireModel]?){
         self.viewController?.displayQuesionsData(Data: userData!)
       }
     
     
     
       func actionContinue() {
           let controller = GeekMeets_StoryBoard.Questionnaire.instantiateViewController(withIdentifier: GeekMeets_ViewController.SelectSocialMedia)
           if let view = self.viewController as? UIViewController
           {
               view.pushVC(controller)
           }
       }
       func actionSkip() {
              let controller = GeekMeets_StoryBoard.LoginSignUp.instantiateViewController(withIdentifier: GeekMeets_ViewController.OTPEnter)
              if let view = self.viewController as? UIViewController
              {
                  view.pushVC(controller)
              }
          }
}
