import UIKit

protocol SelectSocialMediaPresentationProtocol {
    func presentSomething()
    func callQuestionnaireRequest()
    func getQuestionnaireResponse(userData : [QuestionnaireModel]?)
    func actionContinue()
    func actionSkip()
}

class SelectSocialMediaPresenter: SelectSocialMediaPresentationProtocol {
    weak var viewController: SelectSocialMediaProtocol?
    var interactor: SelectSocialMediaInteractorProtocol?
    
    // MARK: Present something
    func presentSomething() {
        
    }
          func callQuestionnaireRequest() {
            self.interactor?.callQuestionnaireApi()
         }
         
         func getQuestionnaireResponse(userData : [QuestionnaireModel]?){
           self.viewController?.displayQuesionsData(Data: userData!)
         }
       
       
       
         func actionContinue() {
             let controller = GeekMeets_StoryBoard.Questionnaire.instantiateViewController(withIdentifier: GeekMeets_ViewController.SelectCategories)
             if let view = self.viewController as? UIViewController
             {
                 view.pushVC(controller)
             }
         }
         func actionSkip() {
                let controller = GeekMeets_StoryBoard.LoginSignUp.instantiateViewController(withIdentifier: GeekMeets_ViewController.OTPEnter)
                if let view = self.viewController as? UIViewController
                {
                    view.pushVC(controller)
                }
            }
  }
